import React, { useState, useEffect, useCallback } from 'react';
import { Mail, RefreshCw, Copy, Trash2, Clock, User, AtSign, CheckCircle } from 'lucide-react';

const API_BASE_URL = 'http://127.0.0.1:8000'; // **Thay đổi URL này nếu backend của bạn chạy ở một địa chỉ khác**

const TempMailApp = () => {
  const [currentEmail, setCurrentEmail] = useState('');
  const [emails, setEmails] = useState([]);
  const [isGenerating, setIsGenerating] = useState(false);
  const [copySuccess, setCopySuccess] = useState(false);
  const [selectedEmail, setSelectedEmail] = useState(null);
  const [backendPassword, setBackendPassword] = useState(''); // Lưu trữ mật khẩu từ backend

  // Refresh inbox
  const refreshInbox = useCallback(async () => {
    if (!currentEmail) return;

    try {
      const response = await fetch(`${API_BASE_URL}/getmail/${currentEmail}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      if (data.code === 0 && data.data && data.data.inbox) {
        // Map backend emails to frontend format
        const fetchedEmails = data.data.inbox.map((mail, index) => ({
          id: index + 1, // You might want to use a unique ID from backend if available
          from: mail.from,
          subject: mail.subject,
          body: mail.body,
          time: new Date().toLocaleString('vi-VN'), // Backend doesn't provide time in this format, use current time for display
          isRead: false // Initially mark as unread
        }));
        setEmails(fetchedEmails);
        // If a selected email exists and is still in the inbox, update it. Otherwise, clear selection.
        if (selectedEmail) {
          const updatedSelected = fetchedEmails.find(e => e.subject === selectedEmail.subject && e.from === selectedEmail.from);
          setSelectedEmail(updatedSelected || null);
        }
      } else {
        console.error("Failed to fetch emails:", data.message);
        setEmails([]);
      }
    } catch (error) {
      console.error("Error fetching emails:", error);
      setEmails([]);
    }
  }, [currentEmail, selectedEmail]);

  // Tạo email mới
  const generateNewEmail = async () => {
    setIsGenerating(true);
    setCurrentEmail(''); // Clear current email while generating
    setEmails([]);       // Clear emails for the new address
    setSelectedEmail(null); // Clear selected email

    try {
      const response = await fetch(`${API_BASE_URL}/createmail`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`Failed to create email: ${errorData.detail || response.statusText}`);
      }

      const data = await response.json();
      if (data.code === 0 && data.data) {
        const newEmailAddress = data.data.name;
        // In a real app, you'd handle the backend password securely, not store it in frontend state directly.
        // For this demo, we'll assume it's implicit or handled by the backend.
        setCurrentEmail(newEmailAddress);
        // Refresh inbox immediately after generating a new email to fetch initial emails (if any)
        await refreshInbox();
      } else {
        console.error("Failed to generate new email:", data.message);
        alert(`Lỗi tạo email: ${data.message}`);
      }
    } catch (error) {
      console.error("Error generating new email:", error);
      alert(`Đã xảy ra lỗi khi tạo email mới: ${error.message}`);
    } finally {
      setIsGenerating(false);
    }
  };

  // Copy email
  const copyToClipboard = () => {
    if (currentEmail) {
      navigator.clipboard.writeText(currentEmail);
      setCopySuccess(true);
      setTimeout(() => setCopySuccess(false), 2000);
    }
  };

  // Xóa email
  const deleteEmail = async (idToDelete) => {
    if (!currentEmail) return;

    try {
      const response = await fetch(`${API_BASE_URL}/deletemail/${currentEmail}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(`Failed to delete email: ${errorData.detail || response.statusText}`);
      }

      const data = await response.json();
      if (data.code === 0) {
        setEmails(emails.filter(email => email.id !== idToDelete));
        if (selectedEmail && selectedEmail.id === idToDelete) {
          setSelectedEmail(null);
        }
        alert('Email đã được xóa thành công!');
        // Consider generating a new email after deleting the current one if that's the desired flow
        // generateNewEmail();
      } else {
        console.error("Failed to delete email:", data.message);
        alert(`Lỗi xóa email: ${data.message}`);
      }
    } catch (error) {
      console.error("Error deleting email:", error);
      alert(`Đã xảy ra lỗi khi xóa email: ${error.message}`);
    }
  };

  // Đánh dấu đã đọc
  const markAsRead = (id) => {
    setEmails(emails.map(email =>
      email.id === id ? { ...email, isRead: true } : email
    ));
  };

  // Khởi tạo email khi component mount
  useEffect(() => {
    generateNewEmail();
  }, []);

  // Set up auto-refresh for inbox
  useEffect(() => {
    const interval = setInterval(() => {
      if (currentEmail) {
        refreshInbox();
      }
    }, 10000); // Refresh every 10 seconds

    return () => clearInterval(interval);
  }, [currentEmail, refreshInbox]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center mb-4">
            <Mail className="w-12 h-12 text-blue-600 mr-3" />
            <h1 className="text-4xl font-bold text-gray-800">Mail Tạm Thời</h1>
          </div>
          <p className="text-gray-600">Tạo email tạm thời để bảo vệ email chính của bạn</p>
        </div>

        {/* Email Generator */}
        <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-xl font-semibold text-gray-800">Email Hiện Tại</h2>
            <button
              onClick={generateNewEmail}
              disabled={isGenerating}
              className="flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 transition-colors"
            >
              <RefreshCw className={`w-4 h-4 mr-2 ${isGenerating ? 'animate-spin' : ''}`} />
              Tạo Mới
            </button>
          </div>

          {isGenerating ? (
            <div className="flex items-center justify-center bg-gray-50 rounded-lg p-4 text-gray-500">
              <RefreshCw className="w-5 h-5 animate-spin mr-3" />
              <span>Đang tạo email mới...</span>
            </div>
          ) : (
            currentEmail && (
              <div className="flex items-center bg-gray-50 rounded-lg p-4">
                <AtSign className="w-5 h-5 text-gray-500 mr-3" />
                <span className="flex-1 font-mono text-lg break-all">{currentEmail}</span>
                <button
                  onClick={copyToClipboard}
                  className="flex items-center px-3 py-2 bg-gray-200 hover:bg-gray-300 rounded-lg transition-colors ml-3"
                >
                  {copySuccess ? (
                    <CheckCircle className="w-4 h-4 text-green-600" />
                  ) : (
                    <Copy className="w-4 h-4" />
                  )}
                  <span className="ml-2">{copySuccess ? 'Đã Copy!' : 'Copy'}</span>
                </button>
              </div>
            )
          )}
        </div>

        <div className="grid md:grid-cols-2 gap-6">
          {/* Inbox */}
          <div className="bg-white rounded-lg shadow-lg">
            <div className="flex items-center justify-between p-6 border-b">
              <h2 className="text-xl font-semibold text-gray-800">
                Hộp Thư ({emails.filter(e => !e.isRead).length} chưa đọc)
              </h2>
              <button
                onClick={refreshInbox}
                className="flex items-center px-3 py-2 text-blue-600 hover:bg-blue-50 rounded-lg transition-colors"
              >
                <RefreshCw className="w-4 h-4 mr-2" />
                Làm Mới
              </button>
            </div>

            <div className="max-h-96 overflow-y-auto">
              {emails.length === 0 ? (
                <div className="p-6 text-center text-gray-500">
                  <Mail className="w-12 h-12 mx-auto mb-3 opacity-50" />
                  <p>Chưa có email nào</p>
                </div>
              ) : (
                emails.map(email => (
                  <div
                    key={email.id}
                    className={`p-4 border-b cursor-pointer hover:bg-gray-50 transition-colors ${
                      !email.isRead ? 'bg-blue-50 border-l-4 border-l-blue-500' : ''
                    } ${selectedEmail?.id === email.id ? 'bg-blue-100' : ''}`}
                    onClick={() => {
                      setSelectedEmail(email);
                      markAsRead(email.id);
                    }}
                  >
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <div className="flex items-center mb-1">
                          <User className="w-4 h-4 text-gray-500 mr-2" />
                          <span className="font-medium text-sm text-gray-700">{email.from}</span>
                          {!email.isRead && (
                            <span className="ml-2 w-2 h-2 bg-blue-600 rounded-full"></span>
                          )}
                        </div>
                        <h3 className="font-semibold text-gray-800 mb-1 line-clamp-1">
                          {email.subject}
                        </h3>
                        <p className="text-sm text-gray-600 line-clamp-2">{email.body}</p>
                        <div className="flex items-center mt-2">
                          <Clock className="w-3 h-3 text-gray-400 mr-1" />
                          <span className="text-xs text-gray-500">{email.time}</span>
                        </div>
                      </div>
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          deleteEmail(email.id);
                        }}
                        className="ml-2 p-1 text-red-500 hover:bg-red-50 rounded"
                      >
                        <Trash2 className="w-4 h-4" />
                      </button>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>

          {/* Email Content */}
          <div className="bg-white rounded-lg shadow-lg">
            <div className="p-6 border-b">
              <h2 className="text-xl font-semibold text-gray-800">Nội Dung Email</h2>
            </div>

            <div className="p-6">
              {selectedEmail ? (
                <div>
                  <div className="mb-4">
                    <div className="flex items-center mb-2">
                      <User className="w-4 h-4 text-gray-500 mr-2" />
                      <span className="text-sm text-gray-600">Từ:</span>
                      <span className="ml-2 font-medium">{selectedEmail.from}</span>
                    </div>
                    <div className="flex items-center mb-2">
                      <AtSign className="w-4 h-4 text-gray-500 mr-2" />
                      <span className="text-sm text-gray-600">Đến:</span>
                      <span className="ml-2 font-medium">{currentEmail}</span>
                    </div>
                    <div className="flex items-center mb-4">
                      <Clock className="w-4 h-4 text-gray-500 mr-2" />
                      <span className="text-sm text-gray-600">Thời gian:</span>
                      <span className="ml-2">{selectedEmail.time}</span>
                    </div>
                  </div>

                  <h3 className="text-lg font-semibold mb-4">{selectedEmail.subject}</h3>
                  <div className="prose max-w-none">
                    <p className="whitespace-pre-wrap">{selectedEmail.body}</p>
                  </div>
                </div>
              ) : (
                <div className="text-center text-gray-500 py-12">
                  <Mail className="w-16 h-16 mx-auto mb-4 opacity-50" />
                  <p>Chọn một email để xem nội dung</p>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Features */}
        <div className="mt-8 bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-lg font-semibold mb-4">Tính Năng</h3>
          <div className="grid md:grid-cols-3 gap-4">
            <div className="text-center p-4">
              <RefreshCw className="w-8 h-8 text-blue-600 mx-auto mb-2" />
              <h4 className="font-semibold mb-1">Tạo Email Nhanh</h4>
              <p className="text-sm text-gray-600">Tạo email tạm thời trong vài giây</p>
            </div>
            <div className="text-center p-4">
              <Mail className="w-8 h-8 text-green-600 mx-auto mb-2" />
              <h4 className="font-semibold mb-1">Nhận Email Tự Động</h4>
              <p className="text-sm text-gray-600">Nhận email ngay lập tức</p>
            </div>
            <div className="text-center p-4">
              <Trash2 className="w-8 h-8 text-red-600 mx-auto mb-2" />
              <h4 className="font-semibold mb-1">Tự Động Xóa</h4>
              <p className="text-sm text-gray-600">Email tự động xóa sau một thời gian</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TempMailApp;
