import { useState } from "react";
import { createTempMail, getInbox, deleteMail } from "../../services/tempmail/mailapi";

export default function TempMail() {
  const [emailInfo, setEmailInfo] = useState(null);
  const [inbox, setInbox] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleCreate = async () => {
    setLoading(true);
    const result = await createTempMail();
    setEmailInfo(result.data);
    setInbox([]);
    setLoading(false);
  };

  const handleFetchInbox = async () => {
    if (!emailInfo) return;
    const result = await getInbox(emailInfo.name);
    setInbox(result.data.inbox);
  };

  const handleDelete = async () => {
    if (!emailInfo) return;
    await deleteMail(emailInfo.name);
    setEmailInfo(null);
    setInbox([]);
  };

  return (
    <div className="max-w-xl mx-auto mt-10 p-4 bg-white shadow rounded">
      <h1 className="text-2xl font-bold mb-4">T·∫°o email t·∫°m th·ªùi</h1>

      <button
        onClick={handleCreate}
        className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
      >
        {loading ? "ƒêang t·∫°o..." : "T·∫°o email m·ªõi"}
      </button>

      {emailInfo && (
        <div className="mt-6">
          <p className="text-lg">üìß Email: <strong>{emailInfo.name}</strong></p>
          <div className="flex gap-2 mt-3">
            <button
              onClick={handleFetchInbox}
              className="px-4 py-1 bg-green-600 text-white rounded hover:bg-green-700"
            >
              Xem inbox
            </button>
            <button
              onClick={handleDelete}
              className="px-4 py-1 bg-red-600 text-white rounded hover:bg-red-700"
            >
              Xo√° email
            </button>
          </div>
        </div>
      )}

      {inbox.length > 0 && (
        <div className="mt-6">
          <h2 className="text-xl font-semibold mb-2">üì• Inbox</h2>
          <ul className="space-y-3">
            {inbox.map((mail, idx) => (
              <li key={idx} className="p-3 border rounded">
                <p><strong>From:</strong> {mail.from}</p>
                <p><strong>Subject:</strong> {mail.subject}</p>
                <p className="text-sm text-gray-600 mt-1">{mail.body}</p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}
