import React, { useState, useEffect, useRef, Suspense } from 'react';

import axios from 'axios';
import TrashIcon from '../../components/icons/TrashIcon';
import CopyIcon from '../../components//icons/CopyIcon';
import ArrowUpOn from '../../components/icons/ArrowUpOn';
import LoadingV2 from '../../components/icons/loadingV2';
import Button from "../../components/Button/Button"



const TempMail = () => {
  const [tempEmail, setTempEmail] = useState(null);
  const [password, setPassword] = useState(null);
  const [messages, setMessages] = useState([]);
  const [selectedMessage, setSelectedMessage] = useState(null); // ✅ khai báo biến này
  const [showEmailEffect, setShowEmailEffect] = useState(true);
  const [loading, setLoading] = useState(true);
  const [emailHistory, setEmailHistory] = useState([]);
  const [remainingTime, setRemainingTime] = useState(0);


  const copyEmailToClipboard = () => {
    navigator.clipboard.writeText(tempEmail);
    alert('Email đã được sao chép!');
  };

  const handleEmailEffectRest = () => {
    setShowEmailEffect(false);
  };

  const createEmail = async () => {
  try {
    setLoading(true);
    setSelectedMessage(null);

    const res = await axios.post("http://localhost:8000/api/createmail");

    const email = res.data.data.name;  // <-- Lấy đúng `email`
    const createdAt = Date.now();

    setTempEmail(email);
    localStorage.setItem("currentTempEmail", email);
    localStorage.setItem("currentTempEmailCreatedAt", createdAt);

    setMessages([]);
    setShowEmailEffect(true);
    setLoading(false);

    const oldEmails = JSON.parse(localStorage.getItem("emailHistory")) || [];
    const updatedEmails = [email, ...oldEmails.filter(e => e !== email)].slice(0, 20);
    localStorage.setItem("emailHistory", JSON.stringify(updatedEmails));
    setEmailHistory(updatedEmails);
  } catch (err) {
    console.error("Lỗi tạo email:", err);
    alert("Không thể tạo email.");
    setLoading(false);
  }
};

  useEffect(() => {
    const savedEmail = localStorage.getItem("currentTempEmail");
    const createdAt = localStorage.getItem("currentTempEmailCreatedAt");
    const now = Date.now();

    if (savedEmail && createdAt) {
      const age = now - parseInt(createdAt, 10);
      if (age < 10 * 60 * 1000) {
        setTempEmail(savedEmail);
      } else {
        localStorage.removeItem("currentTempEmail");
        localStorage.removeItem("currentTempEmailCreatedAt");
      }
    }

    const savedEmails = JSON.parse(localStorage.getItem("emailHistory")) || [];
    setEmailHistory(savedEmails);
  }, []);


  useEffect(() => {
    if (!tempEmail) return;

    const createdAt = parseInt(localStorage.getItem("currentTempEmailCreatedAt") || "0", 10);
    const now = Date.now();
    const maxLife = 10 * 60 * 1000; // 10 phút
    const timeLeft = maxLife - (now - createdAt);

    if (timeLeft <= 0) {
      alert("Email tạm thời đã hết hạn!");
      localStorage.removeItem("currentTempEmail");
      localStorage.removeItem("currentTempEmailCreatedAt");
      setTempEmail(null);
      return;
    }

    setRemainingTime(timeLeft);

    let mailIntervalId;
    let countdownIntervalId;

    const startMailFetching = () => {
      if (!mailIntervalId) {
        mailIntervalId = setInterval(async () => {
          try {
            const mailRes = await axios.get(`http://localhost:8000/api/getmail/${tempEmail}?limit=50`);
            setMessages(mailRes.data.data.inbox);

          } catch (err) {
            console.error("Lỗi khi lấy email:", err);
          }
        }, 5000);
      }
    };

    const stopMailFetching = () => {
      if (mailIntervalId) {
        clearInterval(mailIntervalId);
        mailIntervalId = null;
      }
    };

    const handleVisibilityChange = () => {
      if (document.visibilityState === "visible") {
        startMailFetching();
      } else {
        stopMailFetching();
      }
    };

    document.addEventListener("visibilitychange", handleVisibilityChange);

    if (document.visibilityState === "visible") {
      startMailFetching();
    }

    const timeoutId = setTimeout(() => {
      stopMailFetching();
      alert("Email tạm thời đã hết hạn!");
      setTempEmail(null);
      localStorage.removeItem("currentTempEmail");
      localStorage.removeItem("currentTempEmailCreatedAt");
    }, timeLeft);

    countdownIntervalId = setInterval(() => {
      setRemainingTime(prev => {
        if (prev <= 1000) {
          clearInterval(countdownIntervalId);
          return 0;
        }
        return prev - 1000;
      });
    }, 1000);

    return () => {
      stopMailFetching();
      clearTimeout(timeoutId);
      clearInterval(countdownIntervalId);
      document.removeEventListener("visibilitychange", handleVisibilityChange);
    };
  }, [tempEmail]);


  const handleDeleteEmail = async (emailToDelete) => {
    const confirmDelete = window.confirm(`Bạn có chắc muốn xóa email ${emailToDelete}?`);
    if (!confirmDelete) return;

    try {
      const response = await axios.delete(`http://localhost:8000/api/deletemail/${emailToDelete}`);
      alert(response.data.message);

      // Nếu email đang xem bị xóa, clear nó ra khỏi state
      if (emailToDelete === tempEmail) {
        setTempEmail(null);
        setMessages([]);
        localStorage.removeItem("currentTempEmail");
        localStorage.removeItem("currentTempEmailCreatedAt");
      }

      // Cập nhật lại lịch sử email (xóa email khỏi localStorage và state)
      const updatedHistory = emailHistory.filter((e) => e !== emailToDelete);
      localStorage.setItem("emailHistory", JSON.stringify(updatedHistory));
      setEmailHistory(updatedHistory);
    } catch (err) {
      console.error("Lỗi khi xóa email:", err);
      alert("Xóa email thất bại!");
    }
  };


  const formatTime = (ms) => {
    const totalSeconds = Math.floor(ms / 1000);
    const minutes = String(Math.floor(totalSeconds / 60)).padStart(2, '0');
    const seconds = String(totalSeconds % 60).padStart(2, '0');
    return `${minutes}:${seconds}`;
  };

const handleExtendEmail = (email) => {
  const now = Date.now();
  localStorage.setItem("currentTempEmail", email);
  localStorage.setItem("currentTempEmailCreatedAt", now.toString());
  setTempEmail(email);
};
  return (

  <div className="relative z-20 bg-gray-100 text-gray-700 flex flex-col lg:flex-row w-full max-w-full mx-auto mt-3 gap-4 overflow-hidden">
    <div className="relative flex flex-col justify-center w-full h-screen font-sans bg-gray-100 overflow-hidden">
      <div className="relative z-20 m-5 rounded-lg max-w-3xl bg-white shadow-xl border border-gray-300 overflow-y-auto h-full max-h-[calc(100vh-40px)]">
        <h1 className="text-3xl font-bold text-center mb-6">
          TempMail zikdev.io.vn
        </h1>
        {tempEmail && (
          <div className="remaining-time text-gray-800 text-center">
            ⏰ Email hết hạn sau: <strong>{formatTime(remainingTime)}</strong>
          </div>
        )}

        <div className="mb-6 p-4 border border-gray-300 rounded-md bg-gray-100">
          <p className="mb-2">Địa chỉ email tạm thời của bạn:</p>
          <div className="flex justify-between items-center bg-gray-200 p-3 rounded-md">
            <strong className="break-all">{tempEmail}</strong>
            <button
              onClick={copyEmailToClipboard}
              className="ml-4 bg-gray-300 text-gray-800 px-4 py-2 rounded-md text-sm font-semibold hover:bg-gray-400 transition-colors"
            >
              <CopyIcon size={18} />
            </button>
          </div>
          <div className="flex items-center justify-center p-3">
            <Button
              onClick={createEmail}
              className="mt-4 justify-center w-full bg-gray-300 text-gray-800 px-4 py-2 rounded-md text-base font-semibold hover:bg-gray-400 transition-colors"
            >
              Tạo email mới
            </Button>
          </div>

          <div className="mb-6 p-4 border border-gray-300 rounded-md bg-white">
            <h2 className="text-xl font-bold mb-4 text-center">
              Email đã tạo gần đây
            </h2>

            {emailHistory.length === 0 ? (
              <p className="text-center text-gray-500">Chưa có email nào được tạo.</p>
            ) : (
              <ul className="list-none p-0 max-h-48 overflow-y-auto space-y-2">
                {emailHistory.map((email, index) => (
                  <li
                    key={index}
                    className="bg-gray-200 p-3 rounded-md flex justify-between items-center hover:bg-gray-300 transition-colors"
                  >
                    <span
                      onClick={() => setTempEmail(email)}
                      className="cursor-pointer hover:underline"
                    >
                      {email}
                    </span>
                    <TrashIcon
                      onClick={() => handleDeleteEmail(email)}
                      width={24}
                      height={24}
                    />
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>

        <div className="mb-6 p-4 border border-gray-300 rounded-md bg-white">
          <h2 className="text-2xl font-bold mb-4 text-center">
            Hộp Thư Đến
          </h2>
          {loading ? (
            <div className="flex justify-center items-center text-gray-500">
              <LoadingV2 size={24} />
            </div>
          ) : !tempEmail ? (
            <p className="text-center text-gray-500">Nhấn "Tạo email mới" để bắt đầu.</p>
          ) : messages.length === 0 ? (
            <p className="text-center text-gray-500">Không có tin nhắn nào. Đang chờ email...</p>
          ) : (
            <ul className="list-none p-0">
              {messages.map((msg, index) => (
                <li
                  key={index}
                  onClick={() => setSelectedMessage(msg)}
                  className="bg-gray-100 mb-2 p-3 rounded-md cursor-pointer hover:bg-gray-200 transition-colors"
                >
                  <strong className="text-lg">{msg.subject}</strong>
                  <span className="block text-gray-600 text-sm">Từ: {msg.from}</span>
                  <small className="block text-gray-500 text-xs mt-1">
                    {new Date(msg.timestamp).toLocaleString()}
                  </small>
                </li>
              ))}
            </ul>
          )}
        </div>

        {selectedMessage && (
          <div className="p-4 border border-gray-300 rounded-md bg-gray-100">
            <h2 className="text-2xl font-bold mb-4 text-center">
              Chi Tiết Email
            </h2>
            <p className="mb-2 text-sm">
              <strong className="text-gray-800">Từ:</strong> {selectedMessage.from}
            </p>
            <p className="mb-4 text-sm">
              <strong className="text-gray-800">Chủ đề:</strong> {selectedMessage.subject}
            </p>
            <p className="font-semibold text-gray-800 text-sm mb-2">Nội dung:</p>
            {selectedMessage.html ? (
              <div
                className="bg-white p-3 rounded-md max-h-60 overflow-y-auto text-gray-700"
                dangerouslySetInnerHTML={{ __html: selectedMessage.html }}
              />
            ) : (
              <p className="bg-white p-3 rounded-md max-h-60 overflow-y-auto text-gray-700 whitespace-pre-wrap text-sm">
                {selectedMessage.body}
              </p>
            )}
            <button
              onClick={() => setSelectedMessage(null)}
              className="flex justify-center items-center bg-gray-300 w-full mt-4 hover:bg-gray-400 transition-colors py-2 rounded"
            >
              <ArrowUpOn width={24} height={24} />
            </button>
          </div>
        )}
      </div>
    </div>

    <div className="w-full lg:w-[40%] bg-white rounded-lg p-4 border border-gray-300 shadow-xl overflow-y-auto">
      <h2 className="text-xl font-bold mb-4 text-center">Thông tin Server</h2>
      <ul className="space-y-2 text-sm">
        <li><strong>🟢 Trạng thái:</strong> Đang hoạt động</li>
        <li><strong>🌐 IP:</strong> temp.zikdev.io</li>
        <li><strong>🕒 Uptime:</strong> 12 ngày 5 giờ</li>
        <li><strong>📦 Bộ nhớ:</strong> Loading...</li>
        <li><strong>📨 Email xử lý:</strong> {emailHistory.length} email</li>
      </ul>
    </div>
  </div>
);

};

