import React, { useState } from "react";
import "boxicons/css/boxicons.min.css";
import { Link, Outlet } from "react-router-dom";

const Navside = () => {
  const [asideOpen, setAsideOpen] = useState(true);
  const [profileOpen, setProfileOpen] = useState(false);

  const navLinks = [
    { icon: "bx-home", label: "Dashboard", path: "/" },
    { icon: "bx-cart", label: "Cart", path: "/cart" },
    { icon: "bx-envelope", label: "TempMail", path: "/tempmail" },
    { icon: "bx-shopping-bag", label: "Shopping", path: "/shopping" },
    { icon: "bx-heart", label: "My Favourite", path: "/favourites" },
    { icon: "bx-user", label: "Profile", path: "/profile" },
  ];

  return (
    <div className="min-h-screen w-full bg-gray-100 text-gray-700">
      {/* Header */}
      <header className="flex w-full items-center justify-between border-b-2 border-gray-200 bg-white p-2">
        <div className="flex items-center space-x-2">
          <button
            type="button"
            className="text-3xl"
            onClick={() => setAsideOpen(!asideOpen)}
          >
            <i className="bx bx-menu"></i>
          </button>
          <div>ZIKDEV</div>
        </div>

        {/* Profile */}
        <div className="relative">
          <button
            type="button"
            onClick={() => setProfileOpen(!profileOpen)}
            className="h-9 w-9 overflow-hidden rounded-full"
          >
            <img src="https://plchldr.co/i/40x40?bg=111111" alt="avatar" />
          </button>

          {profileOpen && (
            <div className="absolute right-0 mt-1 w-48 divide-y divide-gray-200 rounded-md border border-gray-200 bg-white shadow-md z-10">
              <div className="flex items-center space-x-2 p-2">
                <img
                  src="https://plchldr.co/i/40x40?bg=111111"
                  alt="avatar"
                  className="h-9 w-9 rounded-full"
                />
                <div className="font-medium">Username</div>
              </div>
              <div className="flex flex-col space-y-3 p-2">
                <Link to="#">My Profile</Link>
                <Link to="#">Edit Profile</Link>
                <Link to="#">Settings</Link>
              </div>
              <div className="p-2">
                <button className="flex items-center space-x-2 hover:text-blue-600">
                  <i className="bx bx-log-out"></i>
                  <span>Log Out</span>
                </button>
              </div>
            </div>
          )}
        </div>
      </header>

      {/* Main Layout */}
      <div className="flex">
        {/* Sidebar */}
        <aside className="w-72 p-4 bg-white border-r border-gray-200">
          {navLinks.map((item) => (
            <Link
              key={item.label}
              to={item.path}
              className="flex items-center space-x-2 px-3 py-2 rounded hover:bg-gray-100"
            >
              <i className={`bx ${item.icon} text-xl`} />
              <span>{item.label}</span>
            </Link>
          ))}
        </aside>

        {/* Content Area */}
        <main className="w-full p-4">
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default Navside;
