import React, { useState, useEffect } from 'react';
import { Mail, RefreshCw, Copy, Trash2, Clock, User, AtSign, CheckCircle, AlertCircle, Loader } from 'lucide-react';

const TempMailApp = () => {
  const [currentEmail, setCurrentEmail] = useState('');
  const [emails, setEmails] = useState([]);
  const [isGenerating, setIsGenerating] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [copySuccess, setCopySuccess] = useState(false);
  const [selectedEmail, setSelectedEmail] = useState(null);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Base URL cho API - thay đổi theo domain của bạn
  const API_BASE_URL = 'http://localhost:8000'; // Thay đổi URL này

  // Tạo chữ ký HMAC cho request
  const createSignature = async (requestTime) => {
    const encoder = new TextEncoder();
    const data = encoder.encode(requestTime);
    const key = encoder.encode('ZYZZ2004@'); // HMAC secret từ backend

    const cryptoKey = await crypto.subtle.importKey(
      'raw',
      key,
      { name: 'HMAC', hash: 'SHA-256' },
      false,
      ['sign']
    );

    const signature = await crypto.subtle.sign('HMAC', cryptoKey, data);
    return Array.from(new Uint8Array(signature))
      .map(b => b.toString(16).padStart(2, '0'))
      .join('');
  };

  // Tạo email mới qua API
  const generateNewEmail = async () => {
    setIsGenerating(true);
    setError('');

    try {
      const requestTime = Date.now().toString();
      const signature = await createSignature(requestTime);

      const response = await fetch(`${API_BASE_URL}/createmail`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          requestTime,
          lang: 'vi',
          signature
        })
      });

      const data = await response.json();

      if (response.ok && data.code === 0) {
        setCurrentEmail(data.data.name);
        setSuccess('Email đã được tạo thành công!');
        setTimeout(() => setSuccess(''), 3000);
        // Tự động tải email sau khi tạo
        setTimeout(() => loadEmails(data.data.name), 2000);
      } else {
        setError(data.detail || 'Không thể tạo email');
      }
    } catch (err) {
      setError('Lỗi kết nối đến server');
      console.error('Error creating email:', err);
    } finally {
      setIsGenerating(false);
    }
  };

  // Tải danh sách email
  const loadEmails = async (emailAddress = currentEmail) => {
    if (!emailAddress) return;

    setIsLoading(true);
    setError('');

    try {
      const response = await fetch(`${API_BASE_URL}/getmail/${emailAddress}?limit=20`);
      const data = await response.json();

      if (response.ok && data.code === 0) {
        // Chuyển đổi format để khớp với giao diện
        const formattedEmails = data.data.inbox.map((email, index) => ({
          id: index + 1,
          from: email.from,
          subject: email.subject || 'Không có tiêu đề',
          body: email.body || 'Không có nội dung',
          html: email.html || '',
          time: new Date().toLocaleString('vi-VN'),
          isRead: false
        }));

        setEmails(formattedEmails.reverse()); // Hiển thị email mới nhất trước
      } else {
        if (response.status === 404) {
          setError('Email chưa được tạo hoặc không tồn tại');
        } else {
          setError(data.detail || 'Không thể tải email');
        }
      }
    } catch (err) {
      setError('Lỗi kết nối đến server');
      console.error('Error loading emails:', err);
    } finally {
      setIsLoading(false);
    }
  };

  // Xóa email
  const deleteCurrentEmail = async () => {
    if (!currentEmail) return;

    if (!confirm('Bạn có chắc chắn muốn xóa email này?')) return;

    setIsLoading(true);
    setError('');

    try {
      const response = await fetch(`${API_BASE_URL}/deletemail/${currentEmail}`, {
        method: 'DELETE'
      });

      const data = await response.json();

      if (response.ok && data.code === 0) {
        setCurrentEmail('');
        setEmails([]);
        setSelectedEmail(null);
        setSuccess('Email đã được xóa thành công!');
        setTimeout(() => setSuccess(''), 3000);
      } else {
        setError(data.detail || 'Không thể xóa email');
      }
    } catch (err) {
      setError('Lỗi kết nối đến server');
      console.error('Error deleting email:', err);
    } finally {
      setIsLoading(false);
    }
  };

  // Copy email
  const copyToClipboard = () => {
    navigator.clipboard.writeText(currentEmail);
    setCopySuccess(true);
    setTimeout(() => setCopySuccess(false), 2000);
  };

  // Đánh dấu đã đọc
  const markAsRead = (id) => {
    setEmails(emails.map(email => 
      email.id === id ? { ...email, isRead: true } : email
    ));
  };

  // Tự động tải email định kỳ
  useEffect(() => {
    if (currentEmail) {
      const interval = setInterval(() => {
        loadEmails();
      }, 30000); // Tải lại mỗi 30 giây

      return () => clearInterval(interval);
    }
  }, [currentEmail]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center mb-4">
            <Mail className="w-12 h-12 text-blue-600 mr-3" />
            <h1 className="text-4xl font-bold text-gray-800">Mail Tạm Thời</h1>
          </div>
          <p className="text-gray-600">Tạo email tạm thời để bảo vệ email chính của bạn</p>
        </div>

        {/* Thông báo */}
        {error && (
          <div className="bg-red-50 border border-red-200 rounded-lg p-4 mb-6 flex items-center">
            <AlertCircle className="w-5 h-5 text-red-500 mr-3" />
            <span className="text-red-700">{error}</span>
            <button 
              onClick={() => setError('')}
              className="ml-auto text-red-500 hover:text-red-700"
            >
              ×
            </button>
          </div>
        )}

        {success && (
          <div className="bg-green-50 border border-green-200 rounded-lg p-4 mb-6 flex items-center">
            <CheckCircle className="w-5 h-5 text-green-500 mr-3" />
            <span className="text-green-700">{success}</span>
            <button 
              onClick={() => setSuccess('')}
              className="ml-auto text-green-500 hover:text-green-700"
            >
              ×
            </button>
          </div>
        )}

        {/* Email Generator */}
        <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-xl font-semibold text-gray-800">Email Hiện Tại</h2>
            <div className="flex space-x-3">
              <button
                onClick={generateNewEmail}
                disabled={isGenerating}
                className="flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 transition-colors"
              >
                <RefreshCw className={`w-4 h-4 mr-2 ${isGenerating ? 'animate-spin' : ''}`} />
                Tạo Mới
              </button>
              {currentEmail && (
                <button
                  onClick={deleteCurrentEmail}
                  disabled={isLoading}
                  className="flex items-center px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 disabled:opacity-50 transition-colors"
                >
                  <Trash2 className="w-4 h-4 mr-2" />
                  Xóa Email
                </button>
              )}
            </div>
          </div>

          {currentEmail ? (
            <div className="flex items-center bg-gray-50 rounded-lg p-4">
              <AtSign className="w-5 h-5 text-gray-500 mr-3" />
              <span className="flex-1 font-mono text-lg">{currentEmail}</span>
              <button
                onClick={copyToClipboard}
                className="flex items-center px-3 py-2 bg-gray-200 hover:bg-gray-300 rounded-lg transition-colors ml-3"
              >
                {copySuccess ? (
                  <CheckCircle className="w-4 h-4 text-green-600" />
                ) : (
                  <Copy className="w-4 h-4" />
                )}
                <span className="ml-2">{copySuccess ? 'Đã Copy!' : 'Copy'}</span>
              </button>
            </div>
          ) : (
            <div className="text-center py-8 text-gray-500">
              <Mail className="w-16 h-16 mx-auto mb-4 opacity-50" />
              <p>Nhấn "Tạo Mới" để bắt đầu</p>
            </div>
          )}
        </div>

        <div className="grid md:grid-cols-2 gap-6">
          {/* Inbox */}
          <div className="bg-white rounded-lg shadow-lg">
            <div className="flex items-center justify-between p-6 border-b">
              <h2 className="text-xl font-semibold text-gray-800">
                Hộp Thư ({emails.filter(e => !e.isRead).length} chưa đọc)
              </h2>
              <button
                onClick={() => loadEmails()}
                disabled={!currentEmail || isLoading}
                className="flex items-center px-3 py-2 text-blue-600 hover:bg-blue-50 rounded-lg transition-colors disabled:opacity-50"
              >
                <RefreshCw className={`w-4 h-4 mr-2 ${isLoading ? 'animate-spin' : ''}`} />
                Làm Mới
              </button>
            </div>

            <div className="max-h-96 overflow-y-auto">
              {!currentEmail ? (
                <div className="p-6 text-center text-gray-500">
                  <Mail className="w-12 h-12 mx-auto mb-3 opacity-50" />
                  <p>Tạo email trước để xem hộp thư</p>
                </div>
              ) : isLoading ? (
                <div className="p-6 text-center text-gray-500">
                  <Loader className="w-8 h-8 mx-auto mb-3 animate-spin" />
                  <p>Đang tải email...</p>
                </div>
              ) : emails.length === 0 ? (
                <div className="p-6 text-center text-gray-500">
                  <Mail className="w-12 h-12 mx-auto mb-3 opacity-50" />
                  <p>Chưa có email nào</p>
                  <button
                    onClick={() => loadEmails()}
                    className="mt-3 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                  >
                    Kiểm tra email
                  </button>
                </div>
              ) : (
                emails.map(email => (
                  <div
                    key={email.id}
                    className={`p-4 border-b cursor-pointer hover:bg-gray-50 transition-colors ${
                      !email.isRead ? 'bg-blue-50 border-l-4 border-l-blue-500' : ''
                    } ${selectedEmail?.id === email.id ? 'bg-blue-100' : ''}`}
                    onClick={() => {
                      setSelectedEmail(email);
                      markAsRead(email.id);
                    }}
                  >
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <div className="flex items-center mb-1">
                          <User className="w-4 h-4 text-gray-500 mr-2" />
                          <span className="font-medium text-sm text-gray-700">{email.from}</span>
                          {!email.isRead && (
                            <span className="ml-2 w-2 h-2 bg-blue-600 rounded-full"></span>
                          )}
                        </div>
                        <h3 className="font-semibold text-gray-800 mb-1 line-clamp-1">
                          {email.subject}
                        </h3>
                        <p className="text-sm text-gray-600 line-clamp-2">
                          {email.body.substring(0, 100)}...
                        </p>
                        <div className="flex items-center mt-2">
                          <Clock className="w-3 h-3 text-gray-400 mr-1" />
                          <span className="text-xs text-gray-500">{email.time}</span>
                        </div>
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>

          {/* Email Content */}
          <div className="bg-white rounded-lg shadow-lg">
            <div className="p-6 border-b">
              <h2 className="text-xl font-semibold text-gray-800">Nội Dung Email</h2>
            </div>

            <div className="p-6">
              {selectedEmail ? (
                <div>
                  <div className="mb-4">
                    <div className="flex items-center mb-2">
                      <User className="w-4 h-4 text-gray-500 mr-2" />
                      <span className="text-sm text-gray-600">Từ:</span>
                      <span className="ml-2 font-medium">{selectedEmail.from}</span>
                    </div>
                    <div className="flex items-center mb-2">
                      <AtSign className="w-4 h-4 text-gray-500 mr-2" />
                      <span className="text-sm text-gray-600">Đến:</span>
                      <span className="ml-2 font-medium">{currentEmail}</span>
                    </div>
                    <div className="flex items-center mb-4">
                      <Clock className="w-4 h-4 text-gray-500 mr-2" />
                      <span className="text-sm text-gray-600">Thời gian:</span>
                      <span className="ml-2">{selectedEmail.time}</span>
                    </div>
                  </div>

                  <h3 className="text-lg font-semibold mb-4">{selectedEmail.subject}</h3>
                  <div className="prose max-w-none">
                    {selectedEmail.html ? (
                      <div 
                        className="border rounded-lg p-4 bg-gray-50"
                        dangerouslySetInnerHTML={{ __html: selectedEmail.html }}
                      />
                    ) : (
                      <p className="whitespace-pre-wrap bg-gray-50 p-4 rounded-lg">
                        {selectedEmail.body}
                      </p>
                    )}
                  </div>
                </div>
              ) : (
                <div className="text-center text-gray-500 py-12">
                  <Mail className="w-16 h-16 mx-auto mb-4 opacity-50" />
                  <p>Chọn một email để xem nội dung</p>
                </div>
              )}
            </div>
          </div>
        </div>

        {/* Instructions */}
        <div className="mt-8 bg-white rounded-lg shadow-lg p-6">
          <h3 className="text-lg font-semibold mb-4">Hướng Dẫn Sử Dụng</h3>
          <div className="grid md:grid-cols-3 gap-4">
            <div className="text-center p-4">
              <RefreshCw className="w-8 h-8 text-blue-600 mx-auto mb-2" />
              <h4 className="font-semibold mb-1">Tạo Email</h4>
              <p className="text-sm text-gray-600">Click "Tạo Mới" để tạo địa chỉ email tạm thời</p>
            </div>
            <div className="text-center p-4">
              <Mail className="w-8 h-8 text-green-600 mx-auto mb-2" />
              <h4 className="font-semibold mb-1">Nhận Email</h4>
              <p className="text-sm text-gray-600">Email sẽ tự động cập nhật mỗi 30 giây</p>
            </div>
            <div className="text-center p-4">
              <Trash2 className="w-8 h-8 text-red-600 mx-auto mb-2" />
              <h4 className="font-semibold mb-1">Xóa Email</h4>
              <p className="text-sm text-gray-600">Xóa email khi không cần thiết nữa</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TempMailApp;
