import React, { useState, useEffect, useRef, Suspense } from 'react';
import { Canvas, useFrame } from '@react-three/fiber';
import { OrbitControls, Text } from '@react-three/drei';
import axios from 'axios';
import TrashIcon from '../../components/icons/TrashIcon';
import CopyIcon from '../../components//icons/CopyIcon';
import ArrowUpOn from '../../components/Common/ArrowUpOn';
import LoadingV2 from '../../components/Common/icons/loadingV2';





// Email arrival effect
function EmailEffect({ position, onRest }) {
  const ref = useRef();
  const startPosition = [position[0], position[1] + 10, position[2]];
  const endPosition = position;
  const speed = 0.1;
  let t = 0;

  useFrame(() => {
    if (ref.current && t < 1) {
      ref.current.position.x = startPosition[0] + (endPosition[0] - startPosition[0]) * t;
      ref.current.position.y = startPosition[1] + (endPosition[1] - startPosition[1]) * t;
      ref.current.position.z = startPosition[2] + (endPosition[2] - startPosition[2]) * t;
      t += speed;
    } else if (t >= 1 && onRest) {
      onRest();
    }
  });

  return (
    <mesh ref={ref} position={startPosition}>
      <boxGeometry args={[1, 0.7, 0.1]} />
      <meshStandardMaterial color="#00e5ff" />
      <Text position={[0, 0, 0.06]} fontSize={0.2} color="black" anchorX="center" anchorY="middle">
        New Mail!
      </Text>
    </mesh>
  );
}

const TempMail = () => {
  const [tempEmail, setTempEmail] = useState(null);
  const [password, setPassword] = useState(null);
  const [messages, setMessages] = useState([]);
  const [selectedMessage, setSelectedMessage] = useState(null); // ✅ khai báo biến này
  const [showEmailEffect, setShowEmailEffect] = useState(true);
  const [loading, setLoading] = useState(true);
  const [emailHistory, setEmailHistory] = useState([]);
  const [remainingTime, setRemainingTime] = useState(0);


  const copyEmailToClipboard = () => {
    navigator.clipboard.writeText(tempEmail);
    alert('Email đã được sao chép!');
  };

  const handleEmailEffectRest = () => {
    setShowEmailEffect(false);
  };

  const createEmail = async () => {
    try {
      setLoading(true);
      setSelectedMessage(null);
      const res = await axios.post("http://localhost:8000/api/createmail",{ mode: "no-cors" });
      const { email } = res.data;
      const createdAt = Date.now(); // Thời gian tạo email

      setTempEmail(email);
      localStorage.setItem("currentTempEmail", email);
      localStorage.setItem("currentTempEmailbody", res.data.body);
      localStorage.setItem("currentTempEmailCreatedAt", createdAt);
      setMessages([]);
      setShowEmailEffect(true);
      setLoading(false);

      const oldEmails = JSON.parse(localStorage.getItem("emailHistory")) || [];
      const updatedEmails = [email, ...oldEmails.filter(e => e !== email)].slice(0, 20); // Giữ tối đa 20 email, không trùng
      localStorage.setItem("emailHistory", JSON.stringify(updatedEmails));
      setEmailHistory(updatedEmails);
    } catch (err) {
      console.error("Lỗi tạo email:", err);
      alert("Không thể tạo email.");
      setLoading(false);
    }
  };

  useEffect(() => {
    const savedEmail = localStorage.getItem("currentTempEmail");
    const createdAt = localStorage.getItem("currentTempEmailCreatedAt");
    const now = Date.now();

    if (savedEmail && createdAt) {
      const age = now - parseInt(createdAt, 10);
      if (age < 10 * 60 * 1000) {
        setTempEmail(savedEmail);
      } else {
        localStorage.removeItem("currentTempEmail");
        localStorage.removeItem("currentTempEmailCreatedAt");
      }
    }

    const savedEmails = JSON.parse(localStorage.getItem("emailHistory")) || [];
    setEmailHistory(savedEmails);
  }, []);


  useEffect(() => {
    if (!tempEmail) return;

    const createdAt = parseInt(localStorage.getItem("currentTempEmailCreatedAt") || "0", 10);
    const now = Date.now();
    const maxLife = 10 * 60 * 1000; // 10 phút
    const timeLeft = maxLife - (now - createdAt);

    if (timeLeft <= 0) {
      alert("Email tạm thời đã hết hạn!");
      localStorage.removeItem("currentTempEmail");
      localStorage.removeItem("currentTempEmailCreatedAt");
      setTempEmail(null);
      return;
    }

    setRemainingTime(timeLeft);

    let mailIntervalId;
    let countdownIntervalId;

    const startMailFetching = () => {
      if (!mailIntervalId) {
        mailIntervalId = setInterval(async () => {
          try {
            const mailRes = await axios.get(`http://localhost:8000/api/getmail/${tempEmail}?limit=50`);
            setMessages(mailRes.data.messages);
          } catch (err) {
            console.error("Lỗi khi lấy email:", err);
          }
        }, 5000);
      }
    };

    const stopMailFetching = () => {
      if (mailIntervalId) {
        clearInterval(mailIntervalId);
        mailIntervalId = null;
      }
    };

    const handleVisibilityChange = () => {
      if (document.visibilityState === "visible") {
        startMailFetching();
      } else {
        stopMailFetching();
      }
    };

    document.addEventListener("visibilitychange", handleVisibilityChange);

    if (document.visibilityState === "visible") {
      startMailFetching();
    }

    const timeoutId = setTimeout(() => {
      stopMailFetching();
      alert("Email tạm thời đã hết hạn!");
      setTempEmail(null);
      localStorage.removeItem("currentTempEmail");
      localStorage.removeItem("currentTempEmailCreatedAt");
    }, timeLeft);

    countdownIntervalId = setInterval(() => {
      setRemainingTime(prev => {
        if (prev <= 1000) {
          clearInterval(countdownIntervalId);
          return 0;
        }
        return prev - 1000;
      });
    }, 1000);

    return () => {
      stopMailFetching();
      clearTimeout(timeoutId);
      clearInterval(countdownIntervalId);
      document.removeEventListener("visibilitychange", handleVisibilityChange);
    };
  }, [tempEmail]);


  const handleDeleteEmail = async (emailToDelete) => {
    const confirmDelete = window.confirm(`Bạn có chắc muốn xóa email ${emailToDelete}?`);
    if (!confirmDelete) return;

    try {
      const response = await axios.delete(`http://localhost:8000/api/deletemail/${emailToDelete}`);
      alert(response.data.message);

      // Nếu email đang xem bị xóa, clear nó ra khỏi state
      if (emailToDelete === tempEmail) {
        setTempEmail(null);
        setMessages([]);
        localStorage.removeItem("currentTempEmail");
        localStorage.removeItem("currentTempEmailCreatedAt");
      }

      // Cập nhật lại lịch sử email (xóa email khỏi localStorage và state)
      const updatedHistory = emailHistory.filter((e) => e !== emailToDelete);
      localStorage.setItem("emailHistory", JSON.stringify(updatedHistory));
      setEmailHistory(updatedHistory);
    } catch (err) {
      console.error("Lỗi khi xóa email:", err);
      alert("Xóa email thất bại!");
    }
  };


  const formatTime = (ms) => {
    const totalSeconds = Math.floor(ms / 1000);
    const minutes = String(Math.floor(totalSeconds / 60)).padStart(2, '0');
    const seconds = String(totalSeconds % 60).padStart(2, '0');
    return `${minutes}:${seconds}`;
  };


  return (
    <>

      <div className="relative z-20 bg-black flex flex-col lg:flex-row w-full max-w-full mx-auto mt-3 gap-4  overflow-hidden">

        <div className="relative flex flex-col  justify-center w-full h-screen font-sans text-white bg-[#0f0f0f] overflow-hidden">



          {/* Main content */}
          <div className="relative z-20 m-5 rounded-lg max-w-3xl bg-[#1a1a1a] bg-opacity-90 shadow-xl border border-gray-700 overflow-y-auto h-full max-h-[calc(100vh-40px)]">
            <h1 className="text-3xl font-bold text-[#737777] text-center mb-6">
              TempMail zikdev.io.vn
            </h1>
            {tempEmail && (
              <div className="remaining-time">
                ⏰ Email hết hạn sau: <strong>{formatTime(remainingTime)}</strong>
              </div>
            )}

            <div className="mb-6 p-4 border border-[#6c7172] rounded-md bg-[#222]">
              <p className="mb-2 text-gray-300">Địa chỉ email tạm thời của bạn:</p>
              <div className="flex justify-between items-center bg-[#111] p-3 rounded-md">
                <strong className="break-all text-white">{tempEmail}</strong>
                <button
                  onClick={copyEmailToClipboard}
                  className="ml-4 bg-[#737777] text-black px-4 py-2 rounded-md text-sm font-semibold hover:bg-[#a5acac] transition-colors"
                >
                  <CopyIcon size={18} />
                </button>
              </div>
              <button
                onClick={createEmail}
                className="mt-4 justify-center w-full bg-[#737777] text-black px-4 py-2 rounded-md text-base font-semibold hover:bg-[#a5acac] transition-colors"
              >
                Tạo email mới
              </button>
              <div className="mb-6 p-4 border border-[#737777] rounded-md bg-[#222]">
                <h2 className="text-xl font-bold text-[#737777] mb-4 text-center">
                  Email đã tạo gần đây
                </h2>

                {emailHistory.length === 0 ? (
                  <p className="text-center text-gray-400">Chưa có email nào được tạo.</p>
                ) : (
                  <ul className="list-none p-0 max-h-48 overflow-y-auto space-y-2">
                    {emailHistory.map((email, index) => (
                      <li
                        key={index}
                        className="bg-[#111] p-3 rounded-md flex justify-between items-center hover:bg-[#1f1f1f] transition-colors"
                      >
                        <span
                          onClick={() => setTempEmail(email)}
                          className="text-white cursor-pointer hover:underline"
                        >
                          {email}
                        </span>

                        <button
                          style={{
                            backgroundColor: '#a5acac',
                          }}
                          onClick={() => handleDeleteEmail(email)}
                          className="ml-4 p-1 rounded hover:bg-[#222] transition"
                          title="Auto delete email in 3 days"
                        >
                          <TrashIcon


                            width={28} height={28} />
                        </button>
                      </li>
                    ))}
                  </ul>
                )}
              </div>
            </div>

            <div className="mb-6 p-4 border border-[#737777] rounded-md bg-[#222]">
              <h2 className="text-2xl font-bold text-[#737777] mb-4 text-center">
                Hộp Thư Đến
              </h2>
              {loading ? (
                <div className="flex text-gray-400 justify-center  items-center">
                  <LoadingV2 size={24}

                  />

                </div>
              ) : !tempEmail ? (
                <p className="text-center text-gray-400">Nhấn "Tạo email mới" để bắt đầu.</p>
              ) : messages.length === 0 ? (
                <p className="text-center text-gray-400">Không có tin nhắn nào. Đang chờ email...</p>
              ) : (

                <ul className="list-none p-0">
                  {messages.map((msg, index) => (
                    <li
                      key={index}
                      onClick={() => setSelectedMessage(msg)} // ✅ sửa đúng
                      className="bg-[#111] mb-2 p-3 rounded-md cursor-pointer hover:bg-[#1f1f1f] transition-colors"
                    >
                      <strong className="text-lg text-white">{msg.subject}</strong>
                      <span className="block text-gray-400 text-sm">Từ: {msg.from}</span>
                      <small className="block text-gray-500 text-xs mt-1">
                        {new Date(msg.timestamp).toLocaleString()}
                      </small>
                    </li>
                  ))}
                </ul>
              )}
            </div>

            {selectedMessage && (
              <div className="p-4 border border-[#737777] rounded-md bg-[#1a1a1a]">
                <h2 className="text-2xl font-bold text-[#737777] mb-4 text-center">
                  Chi Tiết Email
                </h2>
                <p className="mb-2 text-sm">
                  <strong className="text-cyan-200 " >Từ:</strong> {selectedMessage.from}
                </p>
                <p className="mb-4 text-sm">
                  <strong className="text-cyan-200 ">Chủ đề:</strong> {selectedMessage.subject}
                </p>
                <p className="font-semibold text-cyan-200 text-sm">Nội dung:</p>
                {selectedMessage.html ? (
                  <div
                    className="bg-[#111] p-3 rounded-md max-h-60 overflow-y-auto text-gray-200"
                    dangerouslySetInnerHTML={{ __html: selectedMessage.html }}
                  />
                ) : (
                  <p className="bg-[#111] p-3 rounded-md max-h-60 overflow-y-auto text-gray-200 whitespace-pre-wrap text-sm">
                    {selectedMessage.body}
                  </p>
                )}
                <button
                  onClick={() => setSelectedMessage(null)}
                  className='flex justify-center items-center bg-gray-400 w-full'
                >
                  <ArrowUpOn style={{
                    backgroundColor: "#e5e7eb",
                    borderRadius: '50%',
                    padding: '0.5rem',
                  }} width={28} height={28}/>
                </button>
              </div>
            )}
          </div>

        </div>

        <div className="w-full lg:w-[40%] bg-[#1a1a1a] rounded-lg p-4 border border-gray-700 shadow-xl overflow-y-auto">
          <h2 className="text-xl font-bold text-[#737777] mb-4 text-center">Thông tin Server</h2>
          <ul className="space-y-2 text-gray-300 text-sm">
            <li><strong>🟢 Trạng thái:</strong> Đang hoạt động</li>
            <li><strong>🌐 IP:</strong> temp.zikdev.io</li>
            <li><strong>🕒 Uptime:</strong> 12 ngày 5 giờ</li>
            <li><strong>📦 Bộ nhớ:</strong> Loading...</li>
            <li><strong>📨 Email xử lý:</strong> {emailHistory.length} email</li>
            {/* Thêm các dòng tùy ý */}
          </ul>
        </div>
      </div>
    </>
  );
};

export default TempMail;
